(()=>{"use strict";(e=>{const t=document.getElementById("timer-days"),n=document.getElementById("timer-hours"),o=document.getElementById("timer-minutes"),s=document.getElementById("timer-seconds");idInterval=setInterval((()=>{let e=(()=>{let e=(new Date("31 december 2022").getTime()-(new Date).getTime())/1e3;return{timeRemaning:e,days:"Дней "+Math.floor(e/60/60/24),hours:Math.floor(e/60/60%24),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60)}})();e.timeRemaning>0?(t.textContent=e.days,n.textContent=e.hours,o.textContent=e.minutes,s.textContent=e.seconds,e.hours<10&&(n.textContent="0"+e.hours),e.minutes<10&&(o.textContent="0"+e.minutes),e.seconds<10&&(s.textContent="0"+e.seconds)):clearInterval(idInterval)}),1e3)})()})();