(()=>{"use strict";(e=>{const t=document.getElementById("timer-days"),n=document.getElementById("timer-hours"),o=document.getElementById("timer-minutes"),m=document.getElementById("timer-seconds"),s=()=>{let e=(()=>{let e=(new Date("30 december 2022").getTime()-(new Date).getTime())/1e3;return{timeRemaning:e,days:"Дней "+Math.floor(e/60/60/24),hours:Math.floor(e/60/60%24),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60)}})();t.textContent=e.days,n.textContent=e.hours,o.textContent=e.minutes,m.textContent=e.seconds,e.timeRemaning>0&&setTimeout(s,1e3)};s()})()})();